impl LayerBuilder {
    pub fn with_scopes<T: MultiScopeConfig>(
        mut self,
        scopes: impl IntoIterator<Item = ConfigScope>
    ) -> Self {
        for scope in scopes {
            if let Some(path) = T::resolve_path(scope) {
                self = self.with_path(path);
            }
        }
        self
    }
}
